import Form from "./components/Form";
import Note from "./components/Note";
import "./index.css"

class App extends React.Component {

  state = {
    title: "The Message List ðŸ˜²!",
    notesTitle: "â€¢ All Notes â€¢",
    notes: [
      "ðŸŒŸ Good luck out there my child ðŸ¥°",
      "...Oh, I guess you too ðŸ˜• ..."
    ]
  }

  addNote = (row) => {
    this.setState({ notes: this.state.notes.concat(row) })
  }

  deleteNote = (index) => {
    const x = this.state.splice(index, 1)
    console.log('delrow', index, x)
