  }

  render() {
    return (
      <div>
        <div>{this.state.title}</div>

        <Form addNote={this.addNote} />

        <div className="notes">
          <div className="notesTitle">{this.state.notesTitle}</div>

          {this.state.notes.map( (note, i) =>
            <Note
              fromParent={note}
              index={i}
              deleteNote={this.deleteNote}
            />
          )}

        </div>
      </div>
    )
  }


}

export default App;
