    notesTitle: "â€¢ All Notes â€¢",
    notes: [
      "ðŸŒŸ Good luck out there my child ðŸ¥°",
      "...Oh, I guess you too ðŸ˜• ..."
    ]
  }

  return (
    <div>
      <div>{info.title}</div>
      <Form />
      <div className="notes">
        <div className="notesTitle">{info.notesTitle}</div>

        {info.notes.map( note => (
            <div>
              <Note fromParent={note}/>
              <Note fromParent={note}/>
